mapper.insert:
insert into alcohols( al_num, ki_num, ma_num, evt_num, al_name, al_price, al_volume, al_abv, al_stock, al_status
,al_opic
)
values( alcohols_seq.nextval, #{ki_num}, #{ma_num}, #{evt_num}, #{al_name}, #{al_price}, #{al_volume}, #{al_abv}, #{al_stock}, #{al_status}
,'없음'
)

mapper.update:
UPDATE alcohols SET
al_name = #{al_name}, ki_num = #{ki_num}, al_abv = #{al_abv}, al_volume = #{al_volume}, al_price = #{al_price}, al_stock = #{al_stock}, al_status = #{al_status}, evt_num = #{evt_num}
WHERE al_num=#{al_num}

update alcohols
set al_opic='있음'
where al_num=#{al_num}

update alcohols
set al_opic='없음'
where al_num=#{al_num}
